import React, { useState } from 'react';

function CrudApp() {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [editedText, setEditedText] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [fileContent, setFileContent] = useState('');

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        const content = e.target.result;
        const newFile = {
          name: file.name,
          content,
        };
        setFiles([...files, newFile]);
      };

      reader.readAsText(file);
    }
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
    setFileContent(file.content);
    setIsEditing(false);
  };

  const handleFileEdit = () => {
    setIsEditing(true);
  };

  const handleFileSave = () => {
    const updatedFiles = files.map((file) => {
      if (file === selectedFile) {
        return { ...file, content: editedText };
      }
      return file;
    });
    setFiles(updatedFiles);
    setIsEditing(false);
  };

  const handleFileDelete = () => {
    const updatedFiles = files.filter((file) => file !== selectedFile);
    setFiles(updatedFiles);
    setSelectedFile(null);
  };

  return (
    <div>
      <h1>CRUD System for Text Files</h1>
      <input type="file" accept=".txt" onChange={handleFileChange} />

      {selectedFile ? (
        <div>
          <h2>Selected File: {selectedFile.name}</h2>
          {isEditing ? (
            <div>
              <textarea
                value={editedText}
                onChange={(e) => setEditedText(e.target.value)}
              />
              <button onClick={handleFileSave}>Save</button>
            </div>
          ) : (
            <div>
              <div>Content:</div>
              <pre>{fileContent}</pre>
              <button onClick={handleFileEdit}>Edit</button>
              <button onClick={handleFileDelete}>Delete</button>
            </div>
          )}
        </div>
      ) : (
        <div>
          <h2>No File Selected</h2>
        </div>
      )}

      <h2>All Files:</h2>
      <ul>
        {files.map((file, index) => (
          <li key={index} onClick={() => handleFileSelect(file)}>
            {file.name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CrudApp;
